// show and hide puzzles
function changeVis(objSelector, bool) {
    var objNames = PzLib.retrieveObjectNames(objSelector);

    for (var i = 0; i < objNames.length; i++) {
        var objName = objNames[i]
        if (!objName)
            continue;
        var obj = PzLib.getObjectByName(objName);
        if (!obj)
            continue;
        obj.visible = bool;
        obj.resolveMultiMaterial().forEach(function(objR) {
            objR.visible = bool;
        });
    }
}

// setHTMLElemStyle puzzle
function setHTMLElemStyle(prop, value, ids, isParent) {
    var elems = PzLib.getElements(ids, isParent);
    for (var i = 0; i < elems.length; i++) {
        var elem = elems[i];
        if (!elem || !elem.style)
            continue;
        elem.style[prop] = value;
    }
}

// setActiveCamera puzzle
function setActiveCamera(camName) {
    var camera = PzLib.getObjectByName(camName);
    if (!camera || !camera.isCamera || appInstance.getCamera() == camera)
        return;
    appInstance.setCamera(camera);
}

// eventHTMLElem puzzle
function eventHTMLElem(eventType, ids, isParent, callback) {
    var elems = PzLib.getElements(ids, isParent);
    for (var i = 0; i < elems.length; i++) {
        var elem = elems[i];
        if (!elem)
            continue;

        PzLib.bindListener(elem, eventType, callback);
    }
}

// setCSSRuleStyle puzzle
function setCSSRuleStyle(prop, value, id, isParent, mediaRule) {
    const styles = (isParent) ? parent.document.styleSheets : document.styleSheets;
    for (let i = 0; i < styles.length; i++) {
        /**
         * workaround for "DOMException: Failed to read the 'cssRules' property
         * from 'CSSStyleSheet': Cannot access rules"
         */
        let cssRules;
        try {
            cssRules = styles[i].cssRules;
        }
        catch (e) { continue; }

        for (let j = 0; j < cssRules.length; j++) {
            const cssRule = cssRules[j];
            if (!mediaRule && cssRule.selectorText == id)
                cssRule.style[prop] = value;
            else if (mediaRule && cssRule.media && cssRule.media.mediaText == mediaRule) {
                const cssRulesMedia = cssRule.cssRules;
                for (let k = 0; k < cssRulesMedia.length; k++) {
                    if (cssRulesMedia[k].selectorText == id)
                        cssRulesMedia[k].style[prop] = value;
                }
            }
        }
    }
}

// initFullscreen puzzle
function initFullscreen(id, isParent, cb_enter, cb_exit, cb_unavail) {
    var elem = PzLib.getElement(id, isParent);
    if (!elem)
        return;
    var doc = isParent ? parent.document: document;
    if (!(doc.fullscreenEnabled ||
        doc.webkitFullscreenEnabled ||
        doc.mozFullScreenEnabled ||
        doc.msFullscreenEnabled)) {
            cb_unavail();
            return;
    }

    function fullscreenClickCb() {
        event.stopPropagation();
        if (doc.fullscreenElement ||
            doc.webkitFullscreenElement ||
            doc.mozFullScreenElement ||
            doc.msFullscreenElement) {
            exitFullscreen();
        } else {
            requestFullscreen(doc.body);
        }
    }
    PzLib.bindListener(elem, 'click', fullscreenClickCb);

    function changeFullscreen(event) {
        if (doc.fullscreenElement ||
            doc.webkitFullscreenElement ||
            doc.mozFullScreenElement ||
            doc.msFullscreenElement)
            cb_enter(event);
        else
            cb_exit(event);
    }

    PzLib.bindListener(doc, 'webkitfullscreenchange', changeFullscreen);
    PzLib.bindListener(doc, 'mozfullscreenchange', changeFullscreen);
    PzLib.bindListener(doc, 'msfullscreenchange', changeFullscreen);
    PzLib.bindListener(doc, 'fullscreenchange', changeFullscreen);

    function requestFullscreen(fselem) {
        if (fselem.requestFullscreen)
            fselem.requestFullscreen();
        else if (fselem.mozRequestFullScreen)
            fselem.mozRequestFullScreen();
        else if (fselem.webkitRequestFullscreen)
            fselem.webkitRequestFullscreen();
        else if (fselem.msRequestFullscreen)
            fselem.msRequestFullscreen();
    }
    function exitFullscreen() {
        if (doc.exitFullscreen)
            doc.exitFullscreen();
        else if (doc.mozCancelFullScreen)
            doc.mozCancelFullScreen();
        else if (doc.webkitExitFullscreen)
            doc.webkitExitFullscreen();
        else if (doc.msExitFullscreen)
            doc.msExitFullscreen();
    }
}

const intersectPlaneCSS = (() => {
    const coords = new v3d.Vector2();
    const rc = new v3d.Raycaster(); // always check visibility

    return (plane, cssX, cssY, dest) => {
        coords.x = (cssX / appInstance.getWidth()) * 2 - 1;
        coords.y = - (cssY / appInstance.getHeight()) * 2 + 1;
        rc.setFromCamera(coords, appInstance.getCamera(true));
        return rc.ray.intersectPlane(plane, dest);
    };
})();

// dragMove puzzle
_pGlob.dragMoveOrigins = _pGlob.dragMoveOrigins || {};

function dragMove(objSelector, mode, blockId, parentDragOverBlockId) {
    const camera = appInstance.getCamera();
    if (!camera)
        return;

    if (!_pGlob.objDragOverInfoByBlock)
        return;

    const objNames = PzLib.retrieveObjectNames(objSelector);

    const info = _pGlob.objDragOverInfoByBlock[parentDragOverBlockId];
    if (!info) return;

    const draggedObj = PzLib.getObjectByName(info.draggedObjName);
    if (!draggedObj) return;

    if (!(blockId in _pGlob.dragMoveOrigins)) {
        _pGlob.dragMoveOrigins[blockId] = [];
    }
    const posOrigins = _pGlob.dragMoveOrigins[blockId];
    const lenDiff = objNames.length - posOrigins.length;
    for (let i = 0; i < lenDiff; i++) {
        posOrigins.push(new v3d.Vector3());
    }

    for (let i = 0; i < objNames.length; i++) {
        const obj = PzLib.getObjectByName(objNames[i]);
        if (!obj) {
            continue;
        }

        const posOrigin = posOrigins[i];

        const objParented = obj.parent && !obj.parent.isScene;
        const objCamParented = obj.parent && obj.parent.isCamera;

        if (!info.isMoved) {
            // the object position before the first move is used as an initial value
            posOrigin.copy(obj.position);
        }

        const coordSystem = PzLib.getSceneCoordSystem();
        const draggedWorldPos = draggedObj.getWorldPosition(_pGlob.vec3Tmp3);

        if (mode == 'X' || mode == 'Y' || mode == 'Z') {

            let axis = _pGlob.vec3Tmp;
            let coord;

            if (coordSystem == 'Z_UP_RIGHT' && !objCamParented) {
                axis.copy(mode == 'X' ? _pGlob.AXIS_X : (mode == 'Y' ? _pGlob.AXIS_Z : _pGlob.AXIS_Y));
                coord = mode == 'X' ? 'x' : (mode == 'Y' ? 'z' : 'y');
            } else {
                axis.copy(mode == 'X' ? _pGlob.AXIS_X : (mode == 'Y' ? _pGlob.AXIS_Y : _pGlob.AXIS_Z));
                coord = mode == 'X' ? 'x' : (mode == 'Y' ? 'y' : 'z');
            }

            if (objParented)
                axis.transformDirection(obj.matrixWorld);

            const planeNor = camera.getWorldDirection(_pGlob.vec3Tmp2);
            planeNor.cross(axis).cross(axis);

            const plane = _pGlob.planeTmp.setFromNormalAndCoplanarPoint(planeNor, draggedWorldPos);

            const p0 = intersectPlaneCSS(plane, info.downX, info.downY, _pGlob.vec3Tmp);
            const p1 = intersectPlaneCSS(plane, info.currX, info.currY, _pGlob.vec3Tmp2);

            if (p0 && p1) {
                if (objParented) {
                    const invWorld = _pGlob.mat4Tmp.copy(obj.matrixWorld).invert();
                    p0.applyMatrix4(invWorld);
                    p1.applyMatrix4(invWorld);
                }
                obj.position[coord] = posOrigin[coord] + p1[coord] - p0[coord];
            }
        } else if (mode == 'XY' || mode == 'XZ' || mode == 'YZ') {

            let normal = _pGlob.vec3Tmp;
            let coord0, coord1;

            if (coordSystem == 'Z_UP_RIGHT' && !objCamParented) {
                normal.copy(mode == 'XY' ? _pGlob.AXIS_Y : (mode == 'XZ' ? _pGlob.AXIS_Z : _pGlob.AXIS_X));
                coord0 = mode == 'XY' ? 'x' : (mode == 'XZ' ? 'x' : 'y');
                coord1 = mode == 'XY' ? 'z' : (mode == 'XZ' ? 'y' : 'z');
            } else {
                normal.copy(mode == 'XY' ? _pGlob.AXIS_Z : (mode == 'XZ' ? _pGlob.AXIS_Y : _pGlob.AXIS_X));
                coord0 = mode == 'XY' ? 'x' : (mode == 'XZ' ? 'x' : 'y');
                coord1 = mode == 'XY' ? 'y' : (mode == 'XZ' ? 'z' : 'z');
            }

            const plane = _pGlob.planeTmp.setFromNormalAndCoplanarPoint(normal, draggedWorldPos);

            const p0 = intersectPlaneCSS(plane, info.downX, info.downY, _pGlob.vec3Tmp);
            const p1 = intersectPlaneCSS(plane, info.currX, info.currY, _pGlob.vec3Tmp2);
            if (p0 && p1) {
                if (objParented) {
                    const invWorld = _pGlob.mat4Tmp.copy(obj.matrixWorld).invert();
                    p0.applyMatrix4(invWorld);
                    p1.applyMatrix4(invWorld);
                }
                obj.position[coord0] = posOrigin[coord0] + p1[coord0] - p0[coord0];
                obj.position[coord1] = posOrigin[coord1] + p1[coord1] - p0[coord1];
            }
        } else if (mode == 'XYZ') {
            const planeNor = camera.getWorldDirection(_pGlob.vec3Tmp);
            const plane = _pGlob.planeTmp.setFromNormalAndCoplanarPoint(planeNor, draggedWorldPos);

            const p0 = intersectPlaneCSS(plane, info.downX, info.downY, _pGlob.vec3Tmp);
            const p1 = intersectPlaneCSS(plane, info.currX, info.currY, _pGlob.vec3Tmp2);
            if (p0 && p1) {
                if (objParented) {
                    const invWorld = _pGlob.mat4Tmp.copy(obj.matrixWorld).invert();
                    p0.applyMatrix4(invWorld);
                    p1.applyMatrix4(invWorld);
                }
                obj.position.addVectors(posOrigin, p1).sub(p0);
            }
        }
        obj.updateWorldMatrix(false, true);
    }
}

// getObjTransform puzzle
function getObjTransform(objName, isWorldSpace, mode, coord) {
    if (!objName)
        return;
    var obj = PzLib.getObjectByName(objName);
    if (!obj)
        return;

    var coordSystem = PzLib.getSceneCoordSystem();

    var transformVal;

    if (isWorldSpace && obj.parent) {
        if (mode === 'position') {
            transformVal = PzLib.transformCoordsSpace(
                    obj.getWorldPosition(_pGlob.vec3Tmp), 'Y_UP_RIGHT',
                    coordSystem, mode === 'scale');
        } else if (mode === 'rotation') {
            transformVal = PzLib.transformCoordsSpace(
                    obj.getWorldEuler(_pGlob.eulerTmp, 'XYZ'), 'Y_UP_RIGHT',
                    coordSystem, mode === 'scale');
        } else if (mode === 'scale') {
            transformVal = PzLib.transformCoordsSpace(
                    obj.getWorldScale(_pGlob.vec3Tmp), 'Y_UP_RIGHT',
                    coordSystem, mode === 'scale');
        }

    } else if (mode === 'rotation' && coordSystem == 'Z_UP_RIGHT') {
        transformVal = PzLib.transformEulerV3dToBlenderShortest(obj.rotation,
                _pGlob.eulerTmp);

    } else if (mode === 'rotation' && coordSystem == 'Y_UP_RIGHT') {
        // Maya coordinates
        // Use separate rotation interface to fix ambiguous rotations for Maya,
        // might as well do the same for Blender/Max.

        var rotUI = PzLib.RotationInterface.initObject(obj);
        transformVal = rotUI.getUserRotation(_pGlob.eulerTmp);

    } else {
        transformVal = PzLib.transformCoordsSpace(obj[mode].clone(),
                'Y_UP_RIGHT', coordSystem, mode === 'scale');
    }

    if (mode === 'rotation') {
        transformVal.x = v3d.MathUtils.radToDeg(transformVal.x);
        transformVal.y = v3d.MathUtils.radToDeg(transformVal.y);
        transformVal.z = v3d.MathUtils.radToDeg(transformVal.z);
    }

    if (coord == 'xyz') {
        // remove order component for Euler vectors
        return transformVal.toArray().slice(0, 3);
    } else {
        return transformVal[coord];
    }
}

function setMorphFactor(objName, targetName, factor) {

    if (objName && targetName) {
        var obj = PzLib.getObjectByName(objName);
        if (obj) {
            obj.resolveMultiMaterial().forEach(function(objR) {
                if (objR.morphTargetDictionary && targetName in objR.morphTargetDictionary) {
                    var idx = objR.morphTargetDictionary[targetName];
                    objR.morphTargetInfluences[idx] = Number(factor);
                }
            });

            obj.resolveMultiMaterial().forEach(function(objR) {
                objR.getAuxClippingMeshes().forEach(function(objC) {
                    if (objC.morphTargetDictionary && targetName in objC.morphTargetDictionary) {
                        var idx = objC.morphTargetDictionary[targetName];
                        objC.morphTargetInfluences[idx] = Number(factor);
                    }
                });
            });
        }
    }

}

